// MODULES //

import radium from 'radium';
import React from 'react';
import FeedbackButton from './button.js';
import styles from 'styles';


// FEEDBACK BUTTONS //

class FeedbackButtons extends React.Component {
	/*
	* React component render method.
	*/
	render() {
		const props = this.props;
		return (
			<div className="feedbackButtons">
				<div className="fb_line" style={styles.line}>
					<FeedbackButton type="feedback" tooltip="I have feedback." {...props} />
					<FeedbackButton type="understood" tooltip="Makes sense." {...props} />
					<FeedbackButton type="confused" tooltip="I am confused." {...props} />
				</div>
				<div className="clear" style={styles.clear}></div>
				<div id="feedbackForm"></div>
				<div id="response"></div>
			</div>
		);
	}
}


// PROPERTY TYPES //

FeedbackButtons.propTypes = {
	for: React.PropTypes.string.isRequired,
	server: React.PropTypes.string,
	port: React.PropTypes.number,
	clbk: React.PropTypes.func
};


// DEFAULT PROPERTIES //

FeedbackButtons.defaultProps = {
	port: 8000,
	clbk: () => {}
};


// EXPORTS //

export default radium( FeedbackButtons );
