// MODULES //

import React from 'react';


// LESSON //

class Lesson extends React.Component {
	constructor( props ) {
		super( props );
		this.state = props.state;
	}

	componentDidMount() {
		global.lesson = this;
	}

	render() {
		return (
			<div className="Lesson" markdown={1}>
				{this.props.children}
			</div>
		);
	}
} // end class Lesson()


// EXPORTS //

export default Lesson;
