        if ( /runif\(100/.test( str ) ) {
            this.addNotification({
                title: 'Correct.',
                message: 'Your answer is correct!',
                level: 'success',
                position: 'tr'
            });
        } else {
            this.addNotification({
                title: 'Incorrect.',
